import React from 'react';
import './JobList.css';

const JobList = ({ jobs, onDelete, onUpdate }) => {
  return (
    <div className="job-list">
      {jobs.map(job => (
        <div key={job._id} className="job-card">
          <h3>{job.company} - {job.role}</h3>
          <p>Status: {job.status}</p>
          <p>Date: {new Date(job.date).toLocaleDateString()}</p>
          <a href={job.link} target="_blank" rel="noreferrer">üîó View Job</a>
          <select onChange={e => onUpdate(job._id, e.target.value)} value={job.status}>
            <option>Applied</option>
            <option>Interview</option>
            <option>Offer</option>
            <option>Rejected</option>
          </select>
          <button onClick={() => onDelete(job._id)}>üóëÔ∏è Delete</button>
        </div>
      ))}
    </div>
  );
};

export default JobList;
