import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import JobForm from './components/JobForm';
import JobList from './components/JobList';
import FilterBar from './components/FilterBar';
import API from './api';

function App() {
  const [jobs, setJobs] = useState([]);
  const [statusFilter, setStatusFilter] = useState('');
  const [view, setView] = useState('list'); // 'add' or 'list'

  const fetchJobs = async () => {
    try {
      const res = await API.get('/');
      setJobs(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleAdd = async (job) => {
    await API.post('/', job);
    fetchJobs();
    setView('list');
  };

  const handleDelete = async (id) => {
    await API.delete(`/${id}`);
    fetchJobs();
  };

  const handleUpdate = async (id, status) => {
    await API.put(`/${id}`, { status });
    fetchJobs();
  };

  const filteredJobs = statusFilter
    ? jobs.filter((job) => job.status === statusFilter)
    : jobs;

  return (
    <div className="container">
      <Navbar setView={setView} />
      {view === 'add' ? (
        <JobForm onAdd={handleAdd} />
      ) : (
        <>
          <FilterBar setStatusFilter={setStatusFilter} />
          <JobList jobs={filteredJobs} onDelete={handleDelete} onUpdate={handleUpdate} />
        </>
      )}
    </div>
  );
}

export default App;
