import React from 'react';
import API from '../api';
import './JobList.css';

const JobList = ({ jobs, refresh }) => {
  const updateStatus = async (id, status) => {
    await API.put(`/${id}`, { status });
    refresh();
  };

  const deleteJob = async (id) => {
    await API.delete(`/${id}`);
    refresh();
  };

  return (
    <div className="job-list">
      {jobs.map((job) => (
        <div key={job._id} className="job-card">
          <div className="job-header">
            <h3>{job.company}</h3>
            <span className={`status-tag ${job.status.toLowerCase()}`}>
              {job.status}
            </span>
          </div>
          <p><strong>Role:</strong> {job.role}</p>
          <p><strong>Applied On:</strong> {new Date(job.date).toLocaleDateString()}</p>
          <a href={job.link} target="_blank" rel="noreferrer" className="job-link">
            üîó View Job Posting
          </a>

          <div className="job-actions">
            <select
              value={job.status}
              onChange={(e) => updateStatus(job._id, e.target.value)}
            >
              <option>Applied</option>
              <option>Interview</option>
              <option>Offer</option>
              <option>Rejected</option>
            </select>
            <button className="delete-btn" onClick={() => deleteJob(job._id)}>
              ‚ùå Delete
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default JobList;
