import React from 'react';
import API from '../api';

const JobList = ({ jobs, refresh }) => {
  const updateStatus = async (id, status) => {
    await API.put(`/${id}`, { status });
    refresh();
  };

  const deleteJob = async (id) => {
    await API.delete(`/${id}`);
    refresh();
  };

  return (
    <div>
      {jobs.map(job => (
        <div key={job._id} className="job-card">
          <h3>{job.company} - {job.role}</h3>
          <p>Status: {job.status}</p>
          <p>Date: {new Date(job.date).toLocaleDateString()}</p>
          <a href={job.link} target="_blank" rel="noreferrer">Job Link</a>
          <select onChange={e => updateStatus(job._id, e.target.value)} value={job.status}>
            <option>Applied</option>
            <option>Interview</option>
            <option>Offer</option>
            <option>Rejected</option>
          </select>
          <button onClick={() => deleteJob(job._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default JobList;
